abstract class Texto {    public abstract String render();    public abstract String getText();}class TextoNormal extends Texto {    private String texto;    public TextoNormal(String texto) {        this.texto = texto;    }    @Override    public String render() {        return "<span>" + texto + "</span>";    }    @Override    public String getText() {        return texto;    }}abstract class ModificadorTexto extends Texto {    protected Texto texto;    public ModificadorTexto(Texto texto) {        this.texto = texto;    }    @Override    public abstract String render();    @Override    public String getText() {        return texto.getText();    }}class Negrito extends ModificadorTexto {    public Negrito(Texto texto) {        super(texto);    }    @Override    public String render() {        return "<b>" + texto.render() + "</b>";    }}class Italico extends ModificadorTexto {    public Italico(Texto texto) {        super(texto);    }    @Override    public String render() {        return "<i>" + texto.render() + "</i>";    }}class Sublinhado extends ModificadorTexto {    public Sublinhado(Texto texto) {        super(texto);    }    @Override    public String render() {        return "<u>" + texto.render() + "</u>";    }}public class SistemaTexto {    public static void main(String[] args) {        Texto texto1 = new TextoNormal("normal");        System.out.println("Texto Normal: " + texto1.render());        Texto texto2 = new Negrito(new TextoNormal("negrito"));        System.out.println("Texto Negrito: " + texto2.render());        Texto texto3 = new Italico(new TextoNormal("itálico"));        System.out.println("Texto Itálico: " + texto3.render());        Texto texto4 = new Sublinhado(new TextoNormal("sublinhado"));        System.out.println("Texto Sublinhado: " + texto4.render());        Texto texto5 = new Negrito(new Italico(new TextoNormal("negrito e itálico")));        System.out.println("Texto Negrito e Itálico: " + texto5.render());        Texto texto6 = new Sublinhado(new Negrito(new Italico(new TextoNormal("negrito, itálico e sublinhado"))));        System.out.println("Texto Negrito, Itálico e Sublinhado: " + texto6.render());    }}